def test_no_arms(retrieve_images):
    res = ["arm" not in x.abi for x in menu.get_images_info()]
    assert all(res)


def test_some_arms(retrieve_images):
    res = ["arm" not in x.abi for x in menu.get_images_info(arm=True)]
    assert any(res)


def test_has_images(retrieve_images):
    assert len(menu.get_images_info()) > 0


